/* eslint-disable no-console */
import "./globals.css";
import type { Metadata } from "next";
import { redirect } from "next/navigation";
import { Providers } from "./providers";
import { getSupabaseServer } from "../utils/server/supabase";

export const metadata: Metadata = {
  title: "Scenarios",
  // description: "Generated by create next app",
  icons: "/assets/openai.png",
  // todo enable
  // viewport: "width=device-width, height=device-height, initial-scale=1.0",
};

export default async function RootLayout({ children }: { children: React.ReactNode }) {
  const supabase = getSupabaseServer();

  const {
    data: { user },
  } = await supabase.auth.getUser();

  if (!user) {
    redirect("/auth");
    return;
  }

  let { data: profile } = await supabase
    .from("user_profiles")
    .select("user_name")
    .eq("user_id", user.id)
    .single();

  if (!profile) {
    profile = {
      user_name: user.email || "Anon User ü•∑üèæ",
    };

    console.log("creating a user profile for the user because none exists...");
    await supabase.from("user_profiles").upsert({
      ...profile,
      user_id: user.id,
    });
    console.log("user profile created!");
  } else {
    console.log("user profile already exists");
  }

  return (
    <html lang='en' suppressHydrationWarning>
      <body suppressHydrationWarning>
        <Providers user={user} profile={profile}>
          {children}
        </Providers>
      </body>
    </html>
  );
}
